import React from "react";
import { useState } from "react";
import styles from '../styles.js';

export const ColorSliders = ({setColor}) => {

    const [red, setRed] = useState(50);
    const [green, setGreen] = useState(50);
    const [blue, setBlue] = useState(50);

    function changeRed(event) {
        setRed(event.target.value);
        setColor(red, green, blue);
    }
    function changeGreen(event) {
        setGreen(event.target.value);
    }
    function changeBlue(event) {
        setBlue(event.target.value);
    }

    return <>
        <input id="red-slider" type="range" min="0" max="255" value={red} onChange={changeRed}></input>
        <input id="red-slider" type="range" min="0" max="255" value={green} onChange={changeGreen}></input>
        <input id="red-slider" type="range" min="0" max="255" value={blue} onChange={changeBlue}></input>
    </>
}
