import React, { useRef, useEffect } from 'react'
import { useKeyboard } from './hooks/useKeyboard';
import { Turtle } from './Turtle';
import styles from './styles.js';

const TurtleGame = props => {

    // grab deconstruct turtle values
    const {x, y, angle, penDown, penColor, lineWidth} = Turtle();

    // dimensions of canvas
    const width = 800;
    const height = 480;

    // this allows us to access the graphics context
    const canvasRef = useRef(null)

    // this is our game loop
    const draw = (ctx, frameCount) => {
        ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height)
        ctx.fillStyle = '#000000'
        ctx.beginPath()
        ctx.arc(50, 100, 20*Math.sin(frameCount*0.05)**2, 0, 2*Math.PI)
        ctx.fill()
        console.log(moveForward);
    }

    useEffect(() => {
        // get the graphics context
        const canvas = canvasRef.current
        const context = canvas.getContext('2d')
        let frameCount = 0
        let animationFrameId

        // draw the current frame
        const render = () => {
            frameCount++
            draw(context, frameCount)
            animationFrameId = window.requestAnimationFrame(render)
        }
        render()

        return () => {
            window.cancelAnimationFrame(animationFrameId)
        }
    }, [draw]) // called when draw changes

    return <div style={{...styles.canvasWrapper, width: width + 2, height: height + 2 }}>
        <div
            style={{
                ...styles.turtle,
                left: x,
                top: y,
                transform: `rotate(${angle}DEG)`,
            }}
        />
        <canvas ref={canvasRef} {...props}/>
    </div>
}

export default TurtleGame
