            //turtle.drawStar();
        }
    }

    setInterval(() => {
        setValues({x: localTurtle.x, y: localTurtle.y, angle: localTurtle.angle});
    }, 50);

    useEffect(() => {
        updatePosition();
    }, [moveForward])

    useEffect(() => {
        updateRotation();
    }, [turnLeft, turnRight])

    return values;
}

