import React from "react";
import styles from "./styles";
import TurtleGame from "./components/TurtleGame";
import { Navbar } from "./components/Navbar";
import { Footer } from "./components/Footer"

// having an App function makes it easier to segment different parts of the site

const sizeEnum = {
    small: [800, 480],
    medium: [1024, 576],
    large: [1280, 720],
    'absolute unit': [1920, 1080],
};

export default function App() {

    // re-adjust size of canvas
    const changeCanvasSize = (size) => {
        width = sizeEnum[size][0];
        height = sizeEnum[size][1];
    }
    changeCanvasSize(0); // initially canvas is small

    return <div style={styles.root}>
        <Navbar changeCanvasSize={changeCanvasSize}/>

        <div style={styles.column}>
            <TurtleGame width={width} height={height} />

            <Footer width={800} />
        </div>
    </div>
}
