import React, { useRef, useEffect } from 'react'
import { useKeyboard } from './hooks/useKeyboard';
import { Turtle } from './Turtle';
import styles from './styles.js';


const TurtleGame = props => {

    // grab deconstruct turtle values
    const t = Turtle();

    // grab deconstructed list of actions from keyboard listener
    const {moveForward, turnLeft, turnRight, penIsDown, drawingStar} = useKeyboard();

    // dimensions of canvas
    const width = 800;
    const height = 480;

    // this allows us to access the graphics context
    const canvasRef = useRef(null)

    // this is the game loop
    const draw = (ctx, frameCount) => {
        drawLine(ctx, frameCount);
    }

    const drawLine = (ctx, length) => {
        const angleInRadians = (t.angle * Math.PI) / 180;
        let x1 = t.values.x + length * Math.sin(angleInRadians);
        let y1 = t.values.y + length * Math.cos(angleInRadians);
        ctx.beginPath();
        ctx.lineWidth = t.values.lineWidth;
        ctx.strokeStyle = t.values.penColor;
        ctx.moveTo(t.values.x, t.values.y);
        ctx.lineTo(0, 0);
        // ctx.lineTo(x1, y1);
        ctx.stroke();
        t.updatePosition(moveForward);
        console.log(t.values.x, t.values.y);
    }

    useEffect(() => {
        // get the graphics context
        const canvas = canvasRef.current
        const context = canvas.getContext('2d')
        let frameCount = 0
        let animationFrameId

        // draw the current frame
        const render = () => {
            frameCount++
            draw(context, frameCount)
            animationFrameId = window.requestAnimationFrame(render)
        }
        render()

        return () => {
            window.cancelAnimationFrame(animationFrameId)
        }
    }, [draw]) // called when draw changes

    return <div style={{...styles.canvasWrapper, width: width + 2, height: height + 2 }}>
        <div
            style={{
                ...styles.turtle,
                left: t.values.x,
                top: t.values.y,
                transform: `rotate(${t.angle}DEG)`,
            }}
        />
        <canvas ref={canvasRef} {...props}/>
    </div>
}

export default TurtleGame
