import React, { useRef, useState } from 'react'
import { useKeyboard } from './hooks/useKeyboard';
import { Turtle } from './Turtle';
import styles from './styles.js';

export const Canvas = (props) => {

    const [turtleX, setTurtleX] = useState(0);
    const [turtleY, setTurtleY] = useState(0);

    // dimensions of canvas
    const width = 800;
    const height = 480;

    // this allows us to access the graphics context
    const canvasRef = useRef(null)

    function drawLine(length, x0, y0, angle, lineWidth, penColor) {
        const canvas = canvasRef.current
        const ctx = canvas.getContext('2d')

        const angleInRadians = (angle * Math.PI) / 180;
        let x1 = x0 + length * Math.sin(angleInRadians);
        let y1 = y0 + length * Math.cos(angleInRadians);
        ctx.beginPath();
        ctx.lineWidth = lineWidth;
        ctx.strokeStyle = penColor;
        ctx.moveTo(x0, y0);
        ctx.lineTo(0, 0);
        // ctx.lineTo(x1, y1);
        ctx.stroke();
    }

    return <div style={{...styles.canvasWrapper, width: width + 2, height: height + 2 }}>
        <div
            style={{
                ...styles.turtle,
                left: props.turtleX,
                top: props.turtleY,
                transform: `rotate(${t.angle}DEG)`,
            }}
        />
        <canvas ref={canvasRef}/>
    </div>
}
