import React, { useRef, useEffect } from 'react'
import { useKeyboard } from './hooks/useKeyboard';
import { Turtle } from './Turtle';
import styles from './styles.js';

export const Canvas = props => {

    // dimensions of canvas
    const width = 800;
    const height = 480;

    // this allows us to access the graphics context
    const canvasRef = useRef(null)

    function drawLine(length) {
        const canvas = canvasRef.current
        const ctx = canvas.getContext('2d')

        const angleInRadians = (t.angle * Math.PI) / 180;
        let x1 = t.values.x + length * Math.sin(angleInRadians);
        let y1 = t.values.y + length * Math.cos(angleInRadians);
        ctx.beginPath();
        ctx.lineWidth = t.values.lineWidth;
        ctx.strokeStyle = t.values.penColor;
        ctx.moveTo(t.values.x, t.values.y);
        ctx.lineTo(0, 0);
        // ctx.lineTo(x1, y1);
        ctx.stroke();
    }

    return <div style={{...styles.canvasWrapper, width: width + 2, height: height + 2 }}>
        <div
            style={{
                ...styles.turtle,
                left: t.values.x,
                top: t.values.y,
                transform: `rotate(${t.angle}DEG)`,
            }}
        />
        <canvas ref={canvasRef} {...props}/>
    </div>
}
